// controllers/searchQueryController.js
const SearchQuery = require('../models/searchQueryModel');

exports.createSearchQuery = async (req, res) => {
    const { userId, query } = req.body;
    try {
        const result = await SearchQuery.createSearchQuery(userId, query);
        res.status(201).send(result);
    } catch (err) {
        res.status(500).send(err.message);
    }
};
