// controllers/searchResultController.js
const SearchResult = require('../models/searchResultModel');

exports.createSearchResult = async (req, res) => {
    const { searchId, title, description, url } = req.body;
    try {
        const result = await SearchResult.createSearchResult(searchId, title, description, url);
        res.status(201).send(result);
    } catch (err) {
        res.status(500).send(err.message);
    }
};
