// routes/searchRoutes.js
const express = require('express');
const router = express.Router();
const searchQueryController = require('../controllers/searchQueryController');
const searchResultController = require('../controllers/searchResultController');

// Rutas para las consultas de búsqueda
router.post('/queries', searchQueryController.createSearchQuery);  // Crear una nueva consulta de búsqueda

// Rutas para los resultados de búsqueda
router.post('/results', searchResultController.createSearchResult);  // Crear un nuevo resultado de búsqueda

module.exports = router;
